import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 5, id: player.id } },
    });
  };

  const combat = () => {
    hitMonster();
    hitPlayer();
    console.log("Aïe !");
  };



  return (
    <button
      type="button"
      onClick={combat}
      className="btn btn-success m-1"
    >
    </button>
  );
};
export default ButtonCapacity;
