import React from "react";
import { useSelector, useDispatch } from "react-redux";
import ButtonCapacity from "./ButtonCapacity";

const PlayerList = () => {
  const dispatch = useDispatch();
  const players = useSelector((state) => state.players);

  const handlePlayerPlayed = (playerId) => {
    dispatch({
      type: "PLAYER_HAS_PLAYED",
      payload: { playerId },
    });
  };

  return (
    <div className="row">
      {players.map((player) => (
        <div key={player.id} className="col-md-3">
          <div className={`card ${player.hasPlayed ? "played" : ""}`}>
            <div className="card-body">
              <h5 className="card-title">{player.name}</h5>
              <p className="card-text">PV: {player.pv}</p>
              <p className="card-text">Mana: {player.mana}</p>
              <ButtonCapacity player={player} onPlayerPlayed={handlePlayerPlayed} />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PlayerList;
