import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player, ability }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({ type: "HIT_PLAYER", payload: { damage: 5, playerId: player.id } });
  };

  const healPlayer = () => {
    dispatch({ type: "HEAL_PLAYER", payload: { healPlayer: { heal: 5, id: player.id } } });
  };

  const handleAbility = () => {
    // Ajoutez ici la logique spécifique à la capacité
    // Par exemple, vous pouvez vérifier le type de capacité et exécuter l'action appropriée
    if (ability.type === "damage") {
      hitPlayer();
    } else if (ability.type === "heal") {
      healPlayer();
    } else {
      // Autres types de capacité
    }
  };

  return (
    <button
      type="button"
      onClick={() => handleAbility()}
      className="btn btn-success m-1"
    >
      {ability.label} {/* Utilisez la propriété label ou une autre propriété de votre objet ability */}
    </button>
  );
};

export default ButtonCapacity;
