        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }
      ],
    },
    {
      name: "Jessy",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 3,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }

      ],
    },
    {
      name: "Jenny",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 4,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }

      ],
    },
  ],

  monster: {
    pv: 800,
    pvMax: 800,
  },
  currentTurnPlayer: 1,
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "HIT_MONSTER":
      if (!state.players[state.currentTurnPlayer - 1].hasPerformedAction) {
        // Le joueur n'a pas encore effectué d'action
        return {
          ...state,
          monster: {
            ...state.monster,
            pv: Math.max(state.monster.pv - action.payload, 0),
          },
          players: state.players.map((player, index) => ({
            ...player,
            hasPerformedAction: index + 1 === state.currentTurnPlayer,
          })),
        };
      }
      return state;

    case "HIT_PLAYER":
      const { damage, playerId } = action.payload;
      return {
        ...state,
        players: state.players.map((player) =>
          player.id === playerId
            ? {
              ...player,
              pv: Math.max(Math.min(player.pv - damage, player.pvMax), 0), // Limitez les pv entre 0 et pvMax
            }
            : player
        ),
      };

    case "HEAL_PLAYER":
      const { healPlayer } = action.payload;
      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === healPlayer.id) {
            return {
              ...player,
              pv: Math.min(player.pv + healPlayer.heal, player.pvMax), // Limitez les pv à pvMax
            };
          }
          return player;
        }),
      };


    case "FIRE_BALL":
      const currentPlayer = state.players[state.currentTurnPlayer - 1];

      if (!currentPlayer.hasPerformedAction && !currentPlayer.abilities.find(ability => ability.hasUsedAbility && ability.type === "fireBall")) {
        const { fireBall } = action.payload;
        const updatedPlayers = state.players.map(player =>
          player.id === fireBall.id
            ? {
                ...player,
                pv: Math.max(player.pv - fireBall.damage, 0),
                mana: Math.max(player.mana - fireBall.manaCost, 0),
                hasPerformedAction: true,
                abilities: player.abilities.map(ability =>
                  ability.type === "fireBall" ? { ...ability, hasUsedAbility: true } : ability
                ),
              }
            : player
        );

        return {
          ...state,
          players: updatedPlayers,
          monster: {
            ...state.monster,
            pv: Math.max(state.monster.pv - fireBall.damage, 0),
          },
        };
      }
      return state;

    case "MANA_PLAYER":
      const { manaPlayer } = action.payload;
      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === manaPlayer.id) {
            return {
              ...player,
              mana: Math.min(player.mana + manaPlayer.mana, player.manaMax), // Limitez la mana à manaMax
            };
          }
          return player;
        }),
      };

      case "NEXT_TURN":
        const currentPlayerId = state.currentTurnPlayer;
        const nextTurnPlayerId = currentPlayerId % state.players.length + 1;

        const allPlayersPlayed = state.players.every(player => player.hasPerformedAction);

        if (allPlayersPlayed) {
          const resetPlayers = state.players.map(player => ({
            ...player,
            hasPerformedAction: false,
            abilities: player.abilities.map(ability => ({
              ...ability,
              hasUsedAbility: false,
            })),
          }));

          return {
            ...state,
            players: resetPlayers,
            currentTurnPlayer: nextTurnPlayerId,
          };
        } else {
          const updatedPlayers = state.players.map(player =>
            player.id === currentPlayerId ? { ...player, hasPerformedAction: true } : player
          );

          return {
            ...state,
            players: updatedPlayers,
          };
        }





    default:
      return state;
  }
}

export default rootReducer;
