import React, { useEffect, useState } from "react";
import ButtonCapacity from "./ButtonCapacity";
import ProgressBar from "./ProgressBar";
import "./Game.css";

const PlayerCard = ({ player, onPlayerHit }) => {
  const [isLarge, setIsLarge] = useState(true);
  const [animationClass, setAnimationClass] = useState("");
  const [colorClass, setColorClass] = useState("");

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setAnimationClass("");
      setColorClass("");
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [animationClass]);

  const handleHit = () => {
    setAnimationClass("hit");
    setColorClass("hit-color");
    onPlayerHit(player);
  };

  const handleManaRecovery = () => {
    if (player.mana < player.manaMax) {
      const manaRecovered = 10;
      player.mana = Math.min(player.mana + manaRecovered, player.manaMax);
      setAnimationClass("mana-recovery");
      setColorClass("mana-color");
    }
  };

  const handleHealing = () => {
    if (player.pv < player.pvMax) {
      const healingAmount = 20;
      player.pv = Math.min(player.pv + healingAmount, player.pvMax);
      setAnimationClass("healing");
      setColorClass("healing-color");
    }
  };

  const handleSpecialAttack = () => {
    const specialAttackDamage = 30;
    onPlayerHit({ ...player, pv: player.pv - specialAttackDamage });
    setAnimationClass("special-attack");
    setColorClass("special-attack-color");
  };

  return (
    <div
      className={`col-sm-3 card ${isLarge ? "player-large" : ""} ${animationClass} ${colorClass}`}
      id={`joueur${player.id}`}
      onClick={handleHit}
      onContextMenu={(e) => {
        e.preventDefault();
        handleManaRecovery();
        handleHealing();
        handleSpecialAttack();
      }}
      onMouseEnter={() => setIsLarge(false)}
      onMouseLeave={() => setIsLarge(true)}
    >
      <div className='card-body text-center'>
        <img
          className={`player-image ${isLarge ? "player-image-large" : ""}`}
          src={player.image ? require(`../assets/${player.image}`) : ""}
          alt={player.name}
        />

        <h5 className='card-title'>{player.name}</h5>
        <ProgressBar
          pv={player.pv}
          pvMax={player.pvMax}
          faType='fa-heart'
          barName=' : pv '
          bgType='bg-danger'
        />
        <ProgressBar
          pv={player.mana}
          pvMax={player.manaMax}
          faType='fa-fire-alt'
          barName=' : mana '
        />
        <div className='row'>
          {player.abilities.map((ability, index) => (
            <ButtonCapacity key={index} player={player} ability={ability} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
