const initialState = {
  players: [
    {
      name: "John",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 1,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: 30, manaCost: +30, type: "mana" }
      ],
    },
    {
      name: "Jack",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 2,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }
      ],
    },
    {
      name: "Jessy",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 3,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }

      ],
    },
    {
      name: "Jenny",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 4,
      abilities: [
        { name: "Frapper", damage: 10, manaCost: 15, type: "damage" },
        { name: "Guérir", heal: +10, manaCost: 10, type: "heal" },
        { name: "Lancer Attaque Special", damage: 15, manaCost: 30, type: "fireBall" },
        { name: "Recuperation Mana", mana: +30, manaCost: 30, type: "mana" }

      ],
    },
  ],

  monster: {
    pv: 800,
    pvMax: 800,
  },
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "HIT_MONSTER":
      return {
        ...state,
        monster: {
          ...state.monster,
          pv: state.monster.pv - action.payload,
        },
      };
    case "HIT_PLAYER":
      const { damage, playerId } = action.payload;
      const manaCost = 5; // Choisissez le coût de mana approprié

      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === playerId) {
            // Vérifiez si le joueur a suffisamment de mana avant de déduire le coût
            const updatedMana = Math.max(player.mana - manaCost, 0);
            return {
              ...player,
              pv: player.pv - damage,
              mana: updatedMana,
            };
          }
          return player;
        }),
      };
    case "HEAL_PLAYER":
      const { healPlayer } = action.payload;

      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === healPlayer.id) {
            return {
              ...player,
              pv: player.pv + healPlayer.heal,
            };
          }
          return player;
        }),
      };
      case "FIRE_BALL":
        const { fireBall } = action.payload;

        return {
          ...state,
          players: state.players.map((player) => {
            if (player.id === fireBall.id) {
              const updatedMana = Math.max(player.mana - fireBall.manaCost, 0);
              return {
                ...player,
                pv: player.pv - fireBall.damage,
                mana: updatedMana,
              };
            }
            return player;
          }),

          monster: {
            ...state.monster,
            pv: state.monster.pv - fireBall.damage,
          },
        };



      case "MANA_PLAYER":
        const { manaPlayer } = action.payload;

        return {
          ...state,
          players: state.players.map((player) => {
            if (player.id === manaPlayer.id) {
              const updatedMana = Math.min(player.mana + manaPlayer.manaCost, player.manaMax);
              return {
                ...player,
                mana: updatedMana,
              };
            }
            return player;
          }),
        };
        case "PLAYER_WHO_PLAY":
          const { playerWhoPlay } = action.payload;

          return {
            ...state,
            players: state.players.map((player) => {
              if (player.id === playerWhoPlay.id) {
                return {
                  ...player,
                  pv: player.pv + playerWhoPlay.heal,
                };
              }
              return player;
            }),
          };

    default:
      return state;
  }
}

export default rootReducer;
