import React from "react";
import { useDispatch } from "react-redux";

const ButtonAttack = ({ player }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { heal: 10, playerId: player.id },
    });
  };


  const handleAttack = () => {
    hitMonster();
    hitPlayer();
    healPlayer();
  };

  return (
    <button
      type='button'
      onClick={handleAttack}
      className='btn btn-success m-1'
    >
      Frapper <i className='fas fa-bomb'></i> 5 <i className='fas fa-fire-alt'></i> - 5
    </button>
  );
};

export default ButtonAttack;
