import React, { useEffect, useState } from "react";
import "./Game.css";
import Monster from "./Monster";
import PlayerList from "./PlayerList";
import { useDispatch, useSelector } from "react-redux";

const Game = () => {
  const dispatch = useDispatch();
  const turn = useSelector((state) => state.turn);

  const [highlight, setHighlight] = useState(false);
  const [pageReloaded, setPageReloaded] = useState(false);

  const handleNextTurn = () => {
    dispatch({
      type: "NEXT_TURN",
    });
  };

  useEffect(() => {
    setHighlight(true);

    const timeoutId = setTimeout(() => {
      setHighlight(false);
    }, 2000); // Correspond à la durée de l'animation dans le fichier CSS

    if (!pageReloaded) {
      // Recharge la page une seule fois au changement de tour
      window.location.reload();
      setPageReloaded(true);
    }

    return () => clearTimeout(timeoutId);
  }, [turn, pageReloaded]);

  return (
    <div className={`Game ${highlight ? "highlight" : ""}`}>
      <Monster />
      <br />
      <section className="container-fluid">
        <PlayerList />
      </section>
      <button className="btn btn-primary" onClick={handleNextTurn}>
        Next turn (Tour {turn})
      </button>
    </div>
  );
};

export default Game;
