import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player , isHeal }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 5, id: player.id } },
    });
  };

  const handleCapacity = () => {
    if(isHeal){
      healPlayer()
  } else {
    hitMonster();
    hitPlayer();
  }
  }

 /* const combat = () => {
    hitMonster();
    hitPlayer();
    healPlayer();
    console.log("Aïe !");
  };*/



  return (
    <button
    type='button'
    onClick={handleCapacity}
    className='btn btn-success m-1'
  >
    {isHeal ? 'Heal' : 'Hit'}{' '}
    <i className={isHeal ? 'fas fa-heart' : 'fas fa-bomb'}></i> 5{' '}
    <i className='fas fa-fire-alt'></i> - 5
  </button>
);
};
export default ButtonCapacity;
