import React, { useEffect, useState } from "react";
import ButtonCapacity from "./ButtonCapacity";
import ProgressBar from "./ProgressBar";
import "./Game.css";

const PlayerCard = ({ player, onPlayerHit }) => {
  const [isLarge, setIsLarge] = useState(true);
  const [isHit, setIsHit] = useState(false);
  const [isHeal, setIsHeal] = useState(false);

  useEffect(() => {
    if (isHit || isHeal) {
      setTimeout(() => {
        setIsHit(false);
        setIsHeal(false);
      }, 500);
    }
  }, [isHit, isHeal]);

  const handleHit = () => {
    setIsHit(true);
    onPlayerHit(player);
  };

  const handleHeal = () => {
    setIsHeal(true);
    if (player.pv < player.pvMax) {
      onPlayerHit(player);
    }
    // Ajoutez ici la logique de guérison
  };

  return (
    <div
      className={`col-sm-3 card ${isLarge ? "player-large" : ""} ${isHit ? "hit" : ""} ${
        isHit ? "hit-animation" : ""
      } ${isHeal ? "heal" : ""} ${isHeal ? "heal-animation" : ""}`}
      id={`joueur${player.id}`}
      onClick={handleHit}
      onContextMenu={(e) => {
        e.preventDefault();
        handleHeal();
      }}
    >
      <div className='card-body text-center'>
        <img
          className={`player-image ${isLarge ? "player-image-large" : ""}`}
          src={player.image ? require(`../assets/${player.image}`) : ""}
          alt={player.name}
        />

        <h5 className='card-title'>{player.name}</h5>
        <ProgressBar
          pv={player.pv}
          pvMax={player.pvMax}
          faType='fa-heart'
          barName=' : pv '
          bgType='bg-danger'
        />
        <ProgressBar
          pv={player.mana}
          pvMax={player.manaMax}
          faType='fa-fire-alt'
          barName=' : mana '
        />
        <div className='row'>
          {player.abilities.map((ability, index) => (
            <ButtonCapacity key={index} player={player} ability={ability} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
