import React, { useEffect } from "react";
import "./Game.css";
import Monster from "./Monster";
import PlayerList from "./PlayerList";
import { useDispatch, useSelector } from "react-redux";

const Game = () => {
  const dispatch = useDispatch();
  const turn = useSelector((state) => state.turn);

  const handleNextTurn = () => {
    dispatch({
      type: "NEXT_TURN",
    });
  };

  // Use useEffect to automatically trigger the next turn when the turn state changes
  useEffect(() => {
    handleNextTurn();
  }, [turn]); // This will run whenever the 'turn' state changes

  return (
    <div className="Game">
      <Monster />
      <br></br>
      <section className="container-fluid">
        <PlayerList />
      </section>
      {/* Remove the button, as we're handling the next turn automatically */}
    </div>
  );
};

export default Game;
