import React from "react";
import { useDispatch } from "react-redux";
const ButtonCapacity = ({ player, ability = {} }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({
      type: "HIT_MONSTER",
      payload: ability.damage,
    });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: {
        damage: ability.damage,
        playerId: player.id,
      },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: {
        healPlayer: {
          heal: ability.damage,
          id: player.id,
        },
      },
    });
  };

  const handleAbility = () => {
    switch (ability.type) {
      case "damage":
        hitMonster();
        hitPlayer();
        break;
      case "heal":
        healPlayer();
        break;
      default:
        break;
    }
  };

  return (
    <button
      type="button"
      onClick={handleAbility}
      className="btn btn-success m-1"
    >
      {ability.label} {ability.amount !== undefined ? ability.amount : ""}
    </button>
  );
};
export default ButtonCapacity;
