import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player, ability }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: ability.damage });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: ability.damage, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 10, id: player.id, manaCost: ability.manaCost, maxHealth: 100 } },
    });
  };


  const attackSpecial = () => {
    dispatch({
      type: "FIRE_BALL",
      payload: { damage: ability.damage, manaCost: ability.manaCost, playerId: player.id },
    });
  };

  const manaCost = () => {
    dispatch({
      type: "MANA_PLAYER",
      payload: { manaPlayer: { manaCost: ability.manaCost, id: player.id } },
    });
  };


  const handleCapacity = () => {
    switch (ability.type) {
      case "damage":
        hitMonster();
        hitPlayer();
        break;
      case "heal":
        healPlayer();
        break;
      case "fireBall":
        attackSpecial();
        break;
      default:
        break;
    }
  };

  return (
    <button
      type='button'
      onClick={handleCapacity}
      className={`btn btn-success m-1 ${
        ability.type === "heal" ? "btn-heal" : "btn-attack"
      }`}
    >
      {ability.name}{" "}
      <i className={ability.type === "heal" ? "fas fa-heart" : "fas fa-bomb"}></i>{" "}
      {ability.type === "heal" ? `+${ability.heal}` : ability.damage}{" "}
      <i className='fas fa-fire-alt'></i> - {ability.manaCost}
    </button>
  );
};

export default ButtonCapacity;
