import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

const ButtonCapacity = ({ player, ability }) => {
  const dispatch = useDispatch();
  const monsterStatus = useSelector((state) => state.monster);
  const playersStatus = useSelector((state) => state.players);

  const getStatusDisplay = () => {
    const playerStatus = `${player.name} - Health: ${player.pv}/${player.pvMax}, Mana: ${player.mana}/${player.manaMax}`;
    const monsterStatusDisplay = `Monster - Health: ${monsterStatus.pv}/${monsterStatus.pvMax}`;

    // Vous pouvez également afficher les informations de chaque joueur ici si nécessaire
    const playersStatusDisplay = playersStatus.map((player) => {
      return `${player.name} - Health: ${player.pv}/${player.pvMax}, Mana: ${player.mana}/${player.manaMax}`;
    }).join("\n");

    return `${playerStatus}\n${monsterStatusDisplay}\n${playersStatusDisplay}`;
  };

  const displayStatus = () => {
    alert(getStatusDisplay());
  };

  const hitMonster = (damage) => {
    dispatch({ type: "HIT_MONSTER", payload: { monsterDamage: damage } });
  };

  const hitPlayer = (damage) => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage, playerId: player.id, manaReduction: ability.manaCost },
    });
  };

  const healPlayer = (heal) => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal, id: player.id, manaCost: ability.manaCost, maxHealth: 100 } },
    });
  };

  const castFireBall = (damage, manaCost) => {
    dispatch({
      type: "FIRE_BALL",
      payload: { fireBall: { damage, id: player.id, manaCost } },
    });
  };

  const manaPlayer = () => {
    dispatch({
      type: "MANA_PLAYER",
      payload: { manaPlayer: { mana: 30, id: player.id, manaCost: ability.manaCost } },
    });
  };

  const playerIsAlive = () => {
    dispatch({
      type: "PLAYER_IS_ALIVE",
      payload: { playerIsAlive: { id: player.id, manaCost: ability.manaCost } },
    });
  };

  const nextTurn = () => {
    dispatch({ type: "NEXT_TURN" });

    // Vous pouvez également effectuer d'autres actions liées au passage au tour suivant ici

    // Affichez le statut après avoir effectué l'action
    displayStatus();
  };

  const handleCapacity = () => {
    switch (ability.type) {
      case "damage":
        if (player.pv > 0) {
          hitMonster(ability.damage);
        }
        hitPlayer(ability.damage);
        break;
      case "heal":
        healPlayer(ability.heal);
        break;
      case "fireBall":
        castFireBall(ability.damage, ability.manaCost);
        break;
      case "mana":
        manaPlayer();
        break;
      case "playerIsAlive":
        playerIsAlive();
        break;
      case "nextTurn":
        nextTurn();
        break;
      default:
        break;
    }
  };

  const getButtonClassName = () => {
    switch (ability.type) {
      case "heal":
        return "btn btn-danger m-1 btn-heal";
      case "damage":
        return "btn btn-success m-1 btn-attack";
      case "fireBall":
        return "btn btn-warning m-1 btn-fireball";
      case "mana":
        return "btn btn-info m-1 btn-mana";
      default:
        return "btn btn-secondary m-1";
    }
  };

  return (
    <button
      type="button"
      onClick={handleCapacity}
      className={getButtonClassName()}
      disabled={false}
    >
      {ability.name}{" "}
      <i className={ability.type === "heal" ? "fas fa-heart" : "fas fa-bomb"}></i>{" "}
      {ability.type === "heal" ? `+${ability.heal}` : ability.damage}{" "}
      <i className="fas fa-fire-alt"></i> - {ability.manaCost} Mana
    </button>
  );
};

export default ButtonCapacity;
