import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({player}) => {

  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER" ,payload: 5 });

  };

  const hitPlayer = () => {
    dispatch({ type: "HIT_PLAYER", payload: { damage: 5, playerId: player.id } });
  }

  const healPlayer = () => {
    dispatch({ type: "HEAL_PLAYER", payload: { healPlayer: { heal: 5, id: player.id } } });
  }




  const combat = () => {
    hitMonster();
    hitPlayer();
    healPlayer(); 

    console.log("aie !");
  };
  return (

    <div className='col-sm-3'>
      <button
        className='btn btn-outline-danger'
        onClick={combat}
      >
        Combat
      </button>
    </div>


  );

}

export default ButtonCapacity;
