import React, { useState } from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player }) => {
  const dispatch = useDispatch();
  const [isHealing, setIsHealing] = useState(false);

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 5, id: player.id } },
    });
  };

  const combat = () => {
    hitMonster();
    hitPlayer();
    console.log("Aïe !");
  };

  const handleButtonAction = () => {
    if (isHealing) {
      healPlayer();
    } else {
      combat();
    }
  };

  return (
    <button
      className="btn btn-primary col-sm-3 m-1"
      onClick={() => handleButtonAction()}
    >
      {isHealing ? "Heal" : "Hit"} 
    </button>
  );
};

export default ButtonCapacity;
