import React, { useEffect } from "react";
import "./Game.css";
import Monster from "./Monster";
import PlayerList from "./PlayerList";
import { useDispatch, useSelector } from "react-redux";

const Game = () => {
  const dispatch = useDispatch();
  const turn = useSelector((state) => state.turn);

  // Utilisez useEffect pour déclencher automatiquement le tour suivant lorsque l'état 'turn' change
  useEffect(() => {
    dispatch({
      type: "NEXT_TURN",
    });
  }, [turn]); // Cela s'exécutera à chaque changement de l'état 'turn'

  return (
    <div className="Game">
      <Monster />
      <br></br>
      <section className="container-fluid">
        <PlayerList />
      </section>
      {/* Aucun bouton nécessaire, le tour suivant est déclenché automatiquement */}
    </div>
  );
};

export default Game;
