import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({player}) => {

  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER" ,payload: 5 });

  };

  const healPlayer = () => {
    dispatch({ type: "HEAL_PLAYER", payload: { heal: 5, playerId: player.id } });
  }


  const hitPlayer = () => {
    dispatch({ type: "HIT_PLAYER", payload: { damage: 5, playerId: player.id } });
  }


  const handleCapacity = () => {
    if (player.mana >= 5) {
      hitMonster();
      console.log("aie !");
    } else {
      console.log("pas assez de mana");
    }
  }



  const combat = () => {
    hitMonster();
    hitPlayer();
    console.log("aie !");
  };
  return (
      <button className='btn btn-primary m-1' onClick={combat}>
        Combat
      </button>

  );
};

export default ButtonCapacity;
