// ButtonCapacity.js
import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player, ability }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: ability.amount });
  };

  const hitPlayer = () => {
    dispatch({ type: "HIT_PLAYER", payload: { damage: ability.amount, playerId: player.id } });
  };

  const healPlayer = () => {
    dispatch({ type: "HEAL_PLAYER", payload: { healPlayer: { heal: ability.amount, id: player.id } } });
  };

  const handleAbility = () => {
    // Appeler la fonction correspondante en fonction du type de capacité
    if (ability.type === "damage") {
      hitPlayer();
    } else if (ability.type === "heal") {
      healPlayer();
    } else if (ability.type === "hitMonster") {
      hitMonster();
    }
    // Vous pouvez ajouter d'autres types de capacité ici si nécessaire
  };

  return (
    <button
      type="button"
      onClick={() => handleAbility()}
      className="btn btn-success m-1"
    >
      {ability.label} {ability.amount} {/* Utilisez la propriété label et amount ou d'autres propriétés de votre objet ability */}
    </button>
  );
};

export default ButtonCapacity;
