import React from 'react';
import ButtonCapacity from './ButtonCapacity';
import ProgressBar from './ProgressBar';

const PlayerCard = (props) => {
  const { player } = props;

  const abilitiesHeal = player.abilities ? player.abilities.filter((ability) => ability.isHeal) : [];
  const abilitiesHit = player.abilities ? player.abilities.filter((ability) => !ability.isHeal) : [];

  return (
    <div key={player.id} className="col-sm-3 card center" id={`joueur${player.id}`}>
      <div className="card-body text-center">
        <h5 className="card-title">{player.name}</h5>
        <ProgressBar pv={player.pv} pvMax={player.pvMax} faType='fa-heart' barName=' : pv ' bgType='bg-danger' />
        <ProgressBar pv={player.mana} pvMax={player.manaMax} faType='fa-fire-alt' barName=' : mana ' bgType='bg-info' />
        <span className="badge badge-danger ml-2 " id="degatSpanJ1"></span>
        <div className="row">
          <div>
            {/* Boutons pour frapper */}
            {abilitiesHit.map((ability, index) => (
              <ButtonCapacity key={index} player={player} ability={ability} />
            ))}

            {/* Boutons pour guÃ©rir */}
            {abilitiesHeal.map((ability, index) => (
              <ButtonCapacity key={index} player={player} ability={ability} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
