import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({player}) => {

  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER" ,payload: 5 });

  };

  const hitPlayer = () => {
    dispatch({ type: "HIT_PLAYER", payload: { damage: 5, playerId: player.id } });
  }

  const healPlayer = () => {
    dispatch({ type: "HEAL_PLAYER", payload: { healPlayer: { heal: 5, id: player.id } } });
  }




  const combat = () => {
    hitMonster();
    hitPlayer();
    console.log("aie !");
  };
  return (
    <div>
      <button
        type='button'
        onClick={() => combat()}
        className='btn btn-success m-1'
      >
        Hit <i className='fas fa-bomb'></i> 5 <i className='fas fa-fire-alt'></i> - 5
      </button>

      <button
        type='button'
        onClick={() => healPlayer()}
        className='btn btn-primary m-1'
      >
        Heal <i className='fas fa-heart'></i> 10
      </button>
    </div>
  );
}

export default ButtonCapacity;
