import React, { useEffect } from "react";
import "./Game.css";
import Monster from "./Monster";
import PlayerList from "./PlayerList";
import { useDispatch } from "react-redux";

const Game = () => {
  const dispatch = useDispatch();

  // Utilisez useEffect pour déclencher une action au moment du montage du composant
  useEffect(() => {
    dispatch({
      type: "NEXT_TURN",
    });
  }, []); // Tableau vide : effet exécuté une seule fois au montage du composant

  return (
    <div className="Game">
      <Monster />
      <br></br>
      <section className="container-fluid">
        <PlayerList />
      </section>
    </div>
  );
};

export default Game;
