import React, { useEffect, useState } from "react";
import ButtonCapacity from "./ButtonCapacity";
import ProgressBar from "./ProgressBar";
import "./Game.css";
const PlayerCard = ({ player, onPlayerHit }) => {
  const [isLarge, setIsLarge] = useState(true);
  const [isHit, setIsHit] = useState(false);
  const [isManaRecovery, setIsManaRecovery] = useState(false);
  const [isHealing, setIsHealing] = useState(false);
  const [isSpecialAttack, setIsSpecialAttack] = useState(false);
  const [isNormalAttack, setIsNormalAttack] = useState(false);

  useEffect(() => {
    if (isHit || isManaRecovery || isHealing || isSpecialAttack || isNormalAttack) {
      setTimeout(() => {
        setIsHit(false);
        setIsManaRecovery(false);
        setIsHealing(false);
        setIsSpecialAttack(false);
        setIsNormalAttack(false);
      }, 500);
    }
  }, [isHit, isManaRecovery, isHealing, isSpecialAttack, isNormalAttack]);

  const handleHit = () => {
    setIsHit(true);
    onPlayerHit(player);
  };

  const handleManaRecovery = () => {
    if (player.mana < player.manaMax) {
      // Ajoutez la logique de récupération de mana ici
      const manaRecovered = 10; // Par exemple, récupérer 10 points de mana
      player.mana = Math.min(player.mana + manaRecovered, player.manaMax);
      // Vous pouvez également déclencher un effet sonore ou visuel ici
    }
  };

  const handleHealing = () => {
    if (player.pv < player.pvMax) {
      // Ajoutez la logique de guérison ici
      const healingAmount = 20; // Par exemple, guérir 20 points de vie
      player.pv = Math.min(player.pv + healingAmount, player.pvMax);
      // Vous pouvez également déclencher un effet sonore ou visuel ici
    }
  };

  const handleSpecialAttack = () => {
    // Ajoutez la logique d'attaque spéciale ici
    const specialAttackDamage = 30; // Par exemple, infliger 30 points de dégâts avec une attaque spéciale
    onPlayerHit({ ...player, pv: player.pv - specialAttackDamage });
    // Vous pouvez également déclencher un effet sonore ou visuel ici
  };



  return (
    <div
      className={`col-sm-3 card ${isLarge ? 'player-large' : ''} ${isHit ? 'hit' : ''} ${isManaRecovery ? 'mana-recovery' : ''} ${isHealing ? 'healing' : ''} ${isSpecialAttack ? 'special-attack' : ''} ${isNormalAttack ? 'normal-attack' : ''}`}
      id={`joueur${player.id}`}
      onClick={handleHit}
      onContextMenu={(e) => {
        e.preventDefault();
        handleManaRecovery();
        handleHealing();
        handleSpecialAttack();

      }}
    >
      <div className='card-body text-center'>
        <img
          className={`player-image ${isLarge ? 'player-image-large' : ''}`}
          src={player.image ? require(`../assets/${player.image}`) : ''}
          alt={player.name}
        />

        <h5 className='card-title'>{player.name}</h5>
        <ProgressBar
          pv={player.pv}
          pvMax={player.pvMax}
          faType='fa-heart'
          barName=' : pv '
          bgType='bg-danger'
        />
        <ProgressBar
          pv={player.mana}
          pvMax={player.manaMax}
          faType='fa-fire-alt'
          barName=' : mana '
        />
        <div className='row'>
          {player.abilities.map((ability, index) => (
            <ButtonCapacity key={index} player={player} ability={ability} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
