import React, { useEffect } from "react";
import "./Game.css";
import Monster from "./Monster";
import PlayerList from "./PlayerList";
import { useDispatch, useSelector } from "react-redux";

const Game = () => {
  const dispatch = useDispatch();
  const turn = useSelector((state) => state.turn);

  useEffect(() => {
    // Vérifiez si le tour actuel est terminé (par exemple, lorsque tous les joueurs ont joué)
    // Ajoutez ici votre logique pour déterminer si le tour est terminé

    const isTurnOver = true; // Remplacez cela par votre propre logique

    if (isTurnOver) {
      dispatch({
        type: "NEXT_TURN",
      });
    }
  }, [turn, dispatch]); // Déclenche l'effet à chaque changement de 'turn' ou 'dispatch'

  return (
    <div className="Game">
      <Monster />
      <br></br>
      <section className="container-fluid">
        <PlayerList />
      </section>
    </div>
  );
};

export default Game;
