import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player, isHeal }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 10, id: player.id, manaCost: 15 } },
    });
  };

  const fireBall = () => {
    dispatch({
      type: "FIRE_BALL",
      payload: { damage: 15, manaCost: 10, playerId: player.id },
    });
  };

  const handleCapacity = () => {
    if (isHeal) {
      healPlayer();
    } else {
      hitMonster();
      hitPlayer();
      fireBall(); // Ajout de la boule de feu
    }
  };

  return (
    <button
      type='button'
      onClick={handleCapacity}
      className={`btn btn-success m-1 ${
        isHeal ? "btn-heal" : "btn-attack"
      }`}
    >
      {isHeal ? "Guérir" : "Frapper"}{" "}
      <i className={isHeal ? "fas fa-heart" : "fas fa-bomb"}></i>{" "}
      {isHeal ? "+10" : "5"} <i className='fas fa-fire-alt'></i> -{" "}
      {isHeal ? "15" : "5"}
    </button>
  );
};

export default ButtonCapacity;
