const initialState = {
  players: [
    {
      name: "John",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 1,
      abilities: [
        { name: "Heal", damage: 10, manaCost: 20,},

      ],
    },
    {
      name: "Jack",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 2,
    },
    {
      name: "Jessy",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 3,
    },
    {
      name: "Jenny",
      pv: 100,
      pvMax: 100,
      mana: 30,
      manaMax: 30,
      id: 4,
    },
  ],

  monster: {
    pv: 800,
    pvMax: 800,
  },
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "HIT_MONSTER":
      return {
        ...state,
        monster: {
          ...state.monster,
          pv: state.monster.pv - action.payload,
        },
      };
    case "HIT_PLAYER":
      const { damage, playerId } = action.payload;
      const manaCost = 5; // Choisissez le coût de mana approprié

      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === playerId) {
            // Vérifiez si le joueur a suffisamment de mana avant de déduire le coût
            const updatedMana = Math.max(player.mana - manaCost, 0);
            return {
              ...player,
              pv: player.pv - damage,
              mana: updatedMana,
            };
          }
          return player;
        }),
      };
    case "HEAL_PLAYER":
      const { healPlayer } = action.payload;

      return {
        ...state,
        players: state.players.map((player) => {
          if (player.id === healPlayer.id) {
            return {
              ...player,
              pv: player.pv + healPlayer.heal,
            };
          }
          return player;
        }),
      };

    default:
      return state;
  }
}

export default rootReducer;
