let initialState = {
  players: [
    { name: "John", pv: 100, pvMax: 100, mana: 30, manaMax: 30, id: 1 },
    { name: "Jack", pv: 100, pvMax: 100, mana: 30, manaMax: 30, id: 2 },
    { name: "Jessy", pv: 100, pvMax: 100, mana: 30, manaMax: 30, id: 3 },
    { name: "Jenny", pv: 100, pvMax: 100, mana: 30, manaMax: 30, id: 4 },
  ],

  monster: {
    pv: 800,
    pvMax: 800
  },

};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "HIT_MONSTER":
      return {
        ...state,
        monster: {
          ...state.monster,
          pv: state.monster.pv - action.payload
        }
      };
      case "HIT_PLAYER":
        return {
          ...state,
          players: {
            ...state.players,
            [action.payload.player]: {
              ...state.players[0],
              pv: state.players[0].pv - 5
            }
          }
        };
    default:
      return state;
  }


}

export default rootReducer;
