import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player }) => {
  const dispatch = useDispatch();

  const hitMonster = () => {
    dispatch({ type: "HIT_MONSTER", payload: 5 });
  };

  const hitPlayer = () => {
    dispatch({
      type: "HIT_PLAYER",
      payload: { damage: 5, playerId: player.id },
    });
  };

  const healPlayer = () => {
    dispatch({
      type: "HEAL_PLAYER",
      payload: { healPlayer: { heal: 5, id: player.id } },
    });
  };

  const combat = (isMonster) => {
    if (isMonster) {
      hitMonster();
    } else {
      hitPlayer();
    }  if (player.pv < player.pvMax && player.mana > 0) {
      healPlayer();
    }
  }



  return (
    <button
    type='button'
    onClick={() => combat(player.isMonster)}
    className='btn btn-success m-1'>
    hit <i className='fas fa-bomb'></i> 5 <i className='fas fa-fire-alt'></i>{" "}
    - 5
  </button>
);
};
export default ButtonCapacity;
