import React from "react";
import { useDispatch } from "react-redux";

const ButtonCapacity = ({ player, ability }) => {
  const dispatch = useDispatch();

  const handleCapacity = () => {
    switch (ability.type) {
      case "damage":
        if (player.pv > 0) {
          dispatch({
            type: "HIT_MONSTER",
            payload: { monsterDamage: ability.damage }
          });
        }
        dispatch({
          type: "HIT_PLAYER",
          payload: { damage: ability.damage, playerId: player.id },
        });

        break;
      case "heal":
        dispatch({
          type: "HEAL_PLAYER",
          payload: {
            healPlayer: {
              heal: ability.heal,
              id: player.id,
              manaCost: ability.manaCost,
              maxHealth: 100
            }
          }
        });
        break;
      case "fireBall":
        const updatedManaCostFireBall = Math.min(
          ability.manaCost,
          player.mana
        );
        if (player.mana >= updatedManaCostFireBall) {
          dispatch({
            type: "FIRE_BALL",
            payload: {
              fireBall: {
                damage: ability.damage,
                id: player.id,
                manaCost: updatedManaCostFireBall
              }
            }
          });
          dispatch({
            type: "MANA_PLAYER",
            payload: {
              manaPlayer: {
                mana: 30,
                id: player.id,
                manaCost: updatedManaCostFireBall
              }
            }
          });
        } else {
          console.log("Not enough mana to cast the fireball.");
        }
        break;
      case "mana":
        dispatch({
          type: "MANA_PLAYER",
          payload: { manaPlayer: { mana: 30, id: player.id, manaCost: ability.manaCost } }
        });
        break;
      default:
        break;
    }
  };

  const getButtonClassName = () => {
    switch (ability.type) {
      case "heal":
        return "btn btn-danger m-1 btn-heal";
      case "damage":
        return "btn btn-success m-1 btn-attack";
      case "fireBall":
        return "btn btn-warning m-1 btn-fireball";
      case "mana":
        return "btn btn-info m-1 btn-mana";
      default:
        return "btn btn-secondary m-1";
    }
  };

  return (
    <button
      type="button"
      onClick={handleCapacity}
      className={getButtonClassName()}
      disabled={false}
    >
      {ability.name}{" "}
      <i className={ability.type === "heal" ? "fas fa-heart" : "fas fa-bomb"}></i>{" "}
      {ability.type === "heal" ? `+${ability.heal}` : ability.damage}{" "}
      <i className="fas fa-fire-alt"></i> - {ability.manaCost} Mana
    </button>
  );
};

export default ButtonCapacity;
